import React from "react";
import { Link } from "react-router-dom";
import logo from "../images/logo.svg";
import HamburgerMenuIcon from '../images/hamburger_icon_white.svg';

function Header(props) {

  const [mobileWidth, setMobileWidth] = React.useState(false);

  React.useEffect(() => {
    function checkWidth() {
      const windowWidth = window.matchMedia('(max-width: 550px)');
      if (windowWidth.matches) {
        setMobileWidth(true);
      } else {
        setMobileWidth(false);
      }
    }
    checkWidth();
    window.addEventListener('resize', checkWidth);
    return () => window.removeEventListener('resize', checkWidth);
  });

  return (
    <header className="header">
      <img className="logo" src={logo} alt="logo" />
      { mobileWidth && <img className="header__hamburger-logo" src={HamburgerMenuIcon} alt="" /> }
      <div className={`header__info-group `}>
        {props.loggedIn && <p className="header__email">cjmaret@gmail.com</p> }
        <Link className={`header__link ${props.loggedIn ? 'header__link_type_grey' : 'header__link_type_white'}`} to={props.pageLink}>
          {props.linkTitle}
        </Link>
      </div>
    </header>
  );
}

export default Header;
